import React, { useState, useRef, useEffect } from 'react';
import { Play, Square, Mic, Video, Clock } from 'lucide-react';
import { elevenLabsService } from '../services/elevenLabsService';
import { analyzeTranscriptWithGemini } from '../services/voiceAnalysisService';

interface InterviewRecorderProps {
  onStop: (results: any) => void;
}

const QUESTIONS = [
  "Tell me about yourself and your background.",
  "What are your greatest strengths?",
  "Describe a challenging situation you've faced and how you handled it.",
  "Where do you see yourself in 5 years?",
  "Why do you want to work for our company?"
];

export default function InterviewRecorder({ onStop }: InterviewRecorderProps) {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [isRecording, setIsRecording] = useState(false);
  const [isThinking, setIsThinking] = useState(false);
  const [thinkingTime, setThinkingTime] = useState(30);
  const [recordingTime, setRecordingTime] = useState(0);
  const [status, setStatus] = useState('Click "Ask Question" to begin.');
  const [transcript, setTranscript] = useState<string>('');
  const [recordings, setRecordings] = useState<any[]>([]);

  const videoRef = useRef<HTMLVideoElement>(null);
  const mediaRecorderRef = useRef<MediaRecorder | null>(null);
  const chunksRef = useRef<BlobPart[]>([]);
  const streamRef = useRef<MediaStream | null>(null);
  const thinkingTimerRef = useRef<NodeJS.Timeout | null>(null);
  const recordingTimerRef = useRef<NodeJS.Timeout | null>(null);

  useEffect(() => {
    // Setup microphone access
    navigator.mediaDevices.getUserMedia({ audio: true })
      .then((stream) => {
        streamRef.current = stream;
        const mr = new MediaRecorder(stream);
        mr.ondataavailable = (e) => chunksRef.current.push(e.data);
        mr.onstop = async () => {
          const blob = new Blob(chunksRef.current, { type: 'audio/webm' });
          chunksRef.current = [];

          // Transcribe the audio
          setStatus('Transcribing your answer...');
          try {
            const transcription = await elevenLabsService.speechToText(blob);
            const answerText = transcription || '';

            // Store the answer for this question
            setTranscript(prev => prev + `\nQ${currentQuestion + 1}: ${QUESTIONS[currentQuestion]}\nA${currentQuestion + 1}: ${answerText}\n`);
          } catch (error) {
            console.error('Transcription failed:', error);
            setTranscript(prev => prev + `\nQ${currentQuestion + 1}: ${QUESTIONS[currentQuestion]}\nA${currentQuestion + 1}: [Transcription failed]\n`);
          }
        };
        mediaRecorderRef.current = mr;
      })
      .catch(() => setStatus('Microphone permission denied.'));
  }, []);

  const askQuestion = async () => {
    if (currentQuestion >= QUESTIONS.length) return;

    const question = QUESTIONS[currentQuestion];
    setStatus('Interviewer is asking the question...');

    try {
      const audio = await elevenLabsService.generateQuestionAudio(question);
      await audio.play();
      startThinkingTimer();
    } catch (error) {
      console.error('Failed to play question:', error);
      startThinkingTimer();
    }
  };

  const startThinkingTimer = () => {
    setIsThinking(true);
    setThinkingTime(5);
    setStatus('Think about your answer (5 seconds)...');

    thinkingTimerRef.current = setInterval(() => {
      setThinkingTime(prev => {
        if (prev <= 1) {
          clearInterval(thinkingTimerRef.current!);
          setIsThinking(false);
          startRecording();
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
  };

  const skipThinking = () => {
    if (thinkingTimerRef.current) {
      clearInterval(thinkingTimerRef.current);
    }
    setIsThinking(false);
    setThinkingTime(0);
    startRecording();
  };

  const startRecording = () => {
    if (!mediaRecorderRef.current) return;

    // Check if MediaRecorder is already recording
    if (mediaRecorderRef.current.state === 'recording') {
      console.log('MediaRecorder is already recording');
      return;
    }

    setIsRecording(true);
    setRecordingTime(0);
    setStatus('Recording your answer...');
    chunksRef.current = [];

    try {
      mediaRecorderRef.current.start();

      // Start recording timer
      recordingTimerRef.current = setInterval(() => {
        setRecordingTime(prev => prev + 1);
      }, 1000);
    } catch (error) {
      console.error('Failed to start recording:', error);
      setIsRecording(false);
      setStatus('Failed to start recording. Please try again.');
    }
  };

  const stopRecording = async () => {
    if (!mediaRecorderRef.current || !isRecording) return;

    // Check if MediaRecorder is actually recording
    if (mediaRecorderRef.current.state !== 'recording') {
      console.log('MediaRecorder is not recording');
      setIsRecording(false);
      return;
    }

    setIsRecording(false);
    setStatus('Processing your answer...');

    // Stop recording timer
    if (recordingTimerRef.current) {
      clearInterval(recordingTimerRef.current);
    }

    try {
      mediaRecorderRef.current.stop();

      // Wait a moment for transcription to complete
      setTimeout(() => {
        if (currentQuestion < QUESTIONS.length - 1) {
          setCurrentQuestion(prev => prev + 1);
          setStatus('Click "Ask Question" for the next question.');
        } else {
          // All questions completed, analyze with Gemini
          setStatus('Analyzing your interview...');
          analyzeInterview();
        }
      }, 2000);
    } catch (error) {
      console.error('Failed to stop recording:', error);
      setStatus('Failed to stop recording. Please try again.');
    }
  };

  const analyzeInterview = async () => {
    try {
      const analysis = await analyzeTranscriptWithGemini(transcript, {
        company: 'Interview Practice',
        questionCount: QUESTIONS.length
      });

      onStop({
        transcript,
        recordings,
        ...analysis,
        createdAt: new Date().toISOString()
      });
    } catch (error) {
      console.error('Analysis failed:', error);
      onStop({
        transcript,
        recordings,
        summary: 'Analysis failed',
        metrics: { fillerWords: 0, speakingRateWpm: 0 },
        strengths: [],
        improvements: ['Try again'],
        decision: { pass: false, rationale: 'Analysis failed' }
      });
    }
  };

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div className="bg-white rounded-2xl shadow p-6 space-y-6">
      <div className="flex items-center justify-between">
        <h2 className="text-xl font-semibold">Interview Practice</h2>
        <div className="text-sm text-gray-500">
          Question {currentQuestion + 1} of {QUESTIONS.length}
        </div>
      </div>

      {/* Current Question Display */}
      {currentQuestion < QUESTIONS.length && (
        <div className="bg-blue-50 rounded-xl p-4 border border-blue-200">
          <h3 className="font-semibold text-blue-800 mb-2">Current Question:</h3>
          <p className="text-blue-700">{QUESTIONS[currentQuestion]}</p>
        </div>
      )}

      {/* Status Display */}
      <div className="p-4 bg-gray-50 rounded-xl">
        <p className="text-sm text-gray-700">{status}</p>
      </div>

      {/* Timer Display */}
      {(isThinking || isRecording) && (
        <div className="text-center">
          <div className="inline-flex items-center gap-2 bg-yellow-100 text-yellow-800 px-4 py-2 rounded-lg">
            <Clock className="w-4 h-4" />
            <span className="font-semibold">
              {isThinking ? `Think: ${thinkingTime}s` : `Recording: ${formatTime(recordingTime)}`}
            </span>
          </div>
        </div>
      )}

      {/* Video Display */}
      <div className="relative bg-black rounded-lg overflow-hidden">
        <video
          ref={videoRef}
          autoPlay
          muted
          className="w-full h-64 object-cover"
        />
        {isRecording && (
          <div className="absolute top-4 left-4 flex items-center gap-2 bg-red-500 text-white px-3 py-1 rounded-full">
            <div className="w-2 h-2 bg-white rounded-full animate-pulse"></div>
            <span className="text-sm font-medium">REC</span>
          </div>
        )}
      </div>

      {/* Control Buttons */}
      <div className="flex gap-3 justify-center">
        {!isThinking && !isRecording && currentQuestion < QUESTIONS.length && (
          <button
            className="px-6 py-3 rounded-xl bg-indigo-600 text-white font-semibold hover:bg-indigo-700 transition-colors flex items-center gap-2"
            onClick={askQuestion}
          >
            <Play className="w-5 h-5" />
            Ask Question
          </button>
        )}

        {isRecording && (
          <button
            className="px-6 py-3 rounded-xl bg-red-600 text-white font-semibold hover:bg-red-700 transition-colors flex items-center gap-2"
            onClick={stopRecording}
          >
            <Square className="w-5 h-5" />
            Stop Recording
          </button>
        )}
      </div>

      {/* Progress Indicator */}
      <div className="w-full bg-gray-200 rounded-full h-2">
        <div 
          className="bg-indigo-600 h-2 rounded-full transition-all duration-300"
          style={{ width: `${((currentQuestion + (isRecording ? 1 : 0)) / QUESTIONS.length) * 100}%` }}
        ></div>
      </div>
    </div>
  );
}
